scripts.count-3level-files:
node -e "
const fs = require('fs');
const path = require('path');

function countFilesInDirectory(dir) {
  try {
    const items = fs.readdirSync(dir, { withFileTypes: true });
    return items.filter(item => item.isFile()).length;
  } catch (err) {
    return 0;
  }
}

function getDirectoriesAtDepth(baseDir, targetDepth, currentDepth = 0) {
  const dirs = [];

  try {
    const items = fs.readdirSync(baseDir, { withFileTypes: true });

    for (const item of items) {
      if (item.isDirectory() && item.name !== 'node_modules') {
        const fullPath = path.join(baseDir, item.name);
        if (currentDepth === targetDepth) {
          dirs.push(fullPath);
        } else if (currentDepth < targetDepth) {
          dirs.push(...getDirectoriesAtDepth(fullPath, targetDepth, currentDepth + 1));
        }
      }
    }
  } catch (err) {
    // Skip directories we can't read
  }

  return dirs;
}

console.log('ðŸ“ Counting files in directories exactly 3 levels deep from root (excluding node_modules)...\n');

const depth3Dirs = getDirectoriesAtDepth('.', 3);
const directoryResults = [];

console.log('ðŸ” Scanning directories and collecting file counts...');

// Collect all directory results first
for (const dir of depth3Dirs) {
  const fileCount = countFilesInDirectory(dir);
  const relativePath = path.relative('.', dir);
  directoryResults.push({ path: relativePath, fileCount: fileCount });
}

// Sort by file count in descending order (highest first)
directoryResults.sort((a, b) => b.fileCount - a.fileCount);

let totalFiles = directoryResults.reduce((sum, dir) => sum + dir.fileCount, 0);

console.log('\nðŸŽ¯ Found directories at depth 3 (sorted by file count - highest first):');
console.log('='.repeat(60));

// Display sorted results
for (const dir of directoryResults) {
  console.log(\`ðŸ“„ \${dir.path}: \${dir.fileCount} files\`);
}

console.log('='.repeat(60));
console.log(\`ðŸ“Š Summary: \${directoryResults.length} directories, \${totalFiles} total files (node_modules excluded)\`);
"
